mapper.update:
UPDATE 
	post
SET 
	post_id = #{postId}

		,file_url = #{fileUrl}
		,last_updated_time=now()				


		,file_type = #{fileType}
	    ,last_updated_time=now()


		,post_text = #{postText}
		,last_updated_time=now()

WHERE
	post_id = #{postId}

UPDATE post SET post_id = #{postId}
,total_rating=totalRating+#{rating}, ,no_of_ratings=no_of_ratings+1, ,last_updated_time=now()
WHERE post_id = #{postId}
